import java.util.*;public class leftViewBT {       public static void printLeft(Node root){           if(root == null) return;           Queue<Node> q=new LinkedList<>();           q.add(root);           while(q.isEmpty()==false){               int count=q.size();               for(int i=0;i<count;i++){                   Node curr=q.poll();                   if(i==0)                       System.out.print(curr.data+ " ");                   if(curr.left != null)                       q.add(curr.left);                   if(curr.right != null)                       q.add(curr.right);               }           }       }       public static void main(String[] args){           Node root=new Node(30);           root.right=new Node(50);           root.right.left=new Node(60);           root.right.left.right=new Node(10);           printLeft(root);       }}